import { Component } from '@angular/core';
import { Chart } from 'chart.js';
@Component({
  selector: 'app-default-dashboard',
  templateUrl: './default-dashboard.component.html',
  styleUrls: ['./default-dashboard.component.css']
})
export class DefaultDashboardComponent {
  projects: any[] = [];
  projectCounts: { [status: string]: number } = {};
  chartLabels: string[] = [];
  chartData: number[] = [];

  constructor(e) { }

  ngOnInit(): void {
    this.projectService.getProjects().subscribe(
      (data: any[]) => {
        this.projects = data;
        this.countProjectStatus();
        this.renderPieChart();
      },
      (error) => {
        console.error('Error fetching projects:', error);
      }
    );
  }

  countProjectStatus(): void {
    this.projectCounts = {};
    this.projects.forEach(project => {
      const status = project.status;
      this.projectCounts[status] = (this.projectCounts[status] || 0) + 1;
    });
    this.chartLabels = Object.keys(this.projectCounts);
    this.chartData = Object.values(this.projectCounts);
  }

  renderPieChart(): void {
    // Assuming you have a canvas element with id 'pieChart' in your HTML
    const pieChartCanvas = document.getElementById('pieChart') as HTMLCanvasElement;
    const ctx = pieChartCanvas.getContext('2d');

    new Chart(ctx, {
      type: 'pie',
      data: {
        labels: this.chartLabels,
        datasets: [{
          data: this.chartData,
          backgroundColor: [
            'rgba(255, 99, 132, 0.5)',
            'rgba(54, 162, 235, 0.5)',
            'rgba(255, 206, 86, 0.5)',
            // Add more colors as needed
          ],
        }],
      },
      options: {
        // Add chart options here
      },
    });
  }
}
