import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
@Component({
  selector: 'app-manager-profile',
  templateUrl: './manager-profile.component.html',
  styleUrls: ['./manager-profile.component.css']
})
export class ManagerProfileComponent implements OnInit{


  mySet: Set<any>;
  constructor(private formBuilder: FormBuilder,private http : HttpClient){
    this.mySet = new Set();
  }
  profileForm: FormGroup;
  skillIdArray:any;
  ngOnInit(): void {

  this.profileForm=  new FormGroup({
  firstName: new FormControl[''],
  lastName: new FormControl[''],
  email: new FormControl[''],
  department_name: new FormControl[''],
  address: new FormControl[''],
  gender: new FormControl[''],
  phone_no: new FormControl[''],
  skillId: new FormControl[''],
  })

  }

  formData = {
    firstNamePlaceholder: '',
    lastNamePlaceholder: '',
    emailPlaceholder: JSON.parse(sessionStorage.getItem('key')).email,
    phoneNumberPlaceholder: '',
    departmentPlaceholder: '',

  };
  onSubmit() {

  }

  addSkill(){
    let skillbody ={
      "name": this.profileForm.value.skillname
 };
 this.http.post('http://localhost:8081/api/skill/add',skillbody).subscribe((data)=>{
  console.log(data);
  sessionStorage.setItem('skillkey',JSON.stringify(data));
  this.mySet.add( JSON.parse(sessionStorage.getItem('skillkey')).id);
  this.skillIdArray = Array.from(this.mySet);
 })
 console.log("the skill is added");
  }
}
