import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
import { Router } from '@angular/router';
@Component({
  selector: 'app-delete-user',
  templateUrl: './delete-user.component.html',
  styleUrls: ['./delete-user.component.css']
})
export class DeleteUserComponent implements OnInit {

  selecteduserId:any;
  registerForm:FormGroup;
  deleteUserStatus:boolean;
  projectId:number;
  allManagers:any[]=[];
  allUsers:any[]=[];
  projects: any[] = [];
  selectedProjectId: number;
  projectslength:number;
  constructor(private formBuilder: FormBuilder,private http : HttpClient,private router :Router) { }
  ngOnInit(): void {


      const token = JSON.parse(sessionStorage.getItem('key')).token
      const headers = new HttpHeaders({
        'Authorization': `Bearer ${token}`
      });

      this.http.get('http://localhost:8082/api/admin/all', { headers, responseType: 'json' }).subscribe(
       {next: 
        (data: any) => {
          this.allUsers=data;
          this.allManagers.push(...data.filter(item => item.role === 'Manager'));

        },
        error:
        (error) => {
          console.error('Error fetching data:', error);
        }
       } );


       this.http.get<any[]>('http://localhost:8083/api/project/all/projects',{headers}).subscribe((response)=>{
        console.log(response);
        this.projects = response;
        this.projectslength = response.length;
      })

  }

  public DeleteUser(){


    const token = JSON.parse(sessionStorage.getItem('key')).token
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });
      this.http.delete('http://localhost:8082/api/admin/delete/'+this.selecteduserId,{headers,responseType:'text'}).subscribe((data)=>{
        console.log(data);
      })
  }

  public DeleteProject(){
    const token = JSON.parse(sessionStorage.getItem('key')).token
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });
    this.http.delete('http://localhost:8083/api/project/delete/'+this.selectedProjectId,{headers,responseType:'text'}).subscribe((data)=>{
      console.log(data);
    })

  }
}
<div class="container-fluid page">
    <div class="d-flex page__box p-3 mt-2">Delete</div>
    <div class="page__content shadow p-3 position-relative">
      <div class="page__content-data">
<form (ngSubmit)="DeleteUser()">
    <div class="user">
    <h1>Delete User</h1>
    <label>Enter User Id</label>
    <select id="userId" name="userId" [(ngModel)]="selecteduserId">
        <option value="" disabled>Select User</option>
        <option *ngFor="let user of allUsers" [value]="user.user_id">{{ user.user_id }}</option>
      </select>
    <br>
    <button class="user-button" type="submit">Confirm</button>
    <p *ng> User deleted Successfully</p>
    </div>
</form>


<form (ngSubmit)="DeleteProject()">
    <div class="user">
      <h1>Delete Project</h1>
      <label>Enter Project Id</label>
      <select id="projectId" name="projectId" [(ngModel)]="selectedProjectId">
        <option value="" disabled>Select Project</option>
        <option *ngFor="let project of projects" [value]="project.project_id">{{ project.project_id }}</option>
      </select>
      <br>
      <button class="user-button" type="submit">Confirm</button>
    </div>
  </form>
</div>
</div>
</div>
