import { Component } from '@angular/core';
import { Chart } from 'chart.js';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
@Component({
  selector: 'app-default-dashboard',
  templateUrl: './default-dashboard.component.html',
  styleUrls: ['./default-dashboard.component.css']
})
export class DefaultDashboardComponent {
  chartdata : any ;
  labeldata: any[]=[];
  taskCounts: any = {};
  projectCounts : any = {};

  chartLabels = [];
  chartData = [];
  constructor(private http:HttpClient){}
  ngOnInit(): void {

    const token = JSON.parse(sessionStorage.getItem('key')).token;
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });
    this.http.get<any[]>('http://localhost:8083/api/project/details',{headers,responseType:'json'}).subscribe((response)=>{
      console.log(response);
      sessionStorage.setItem('projectkey',JSON.stringify(response));
      this.chartdata = response;
    })
    // Parse project data from sessionStorage


    // Initialize an empty object to store project counts by status


    // Render the pie chart
    this.renderPieChart();
  }

  renderPieChart() {
    const ctx = document.getElementById('pieChart') as HTMLCanvasElement;
    const pieChart = new Chart(ctx, {
      type: 'pie',
      data: {
        labels: this.chartLabels,
        datasets: [{
          label: 'Project Status',
          data: this.chartData,
          backgroundColor: [
            'rgba(255, 99, 132, 0.5)', // COMPLETED
            'rgba(54, 162, 235, 0.5)', // IN_PROGRESS
            'rgba(255, 206, 86, 0.5)', // ON_HOLD
          ],
          borderColor: [
            'rgba(255, 99, 132, 1)',
            'rgba(54, 162, 235, 1)',
            'rgba(255, 206, 86, 1)',
          ],
          borderWidth: 1
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false
      }
    });
  }
}


