import { Component,OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
import { Router } from '@angular/router';
@Component({
  selector: 'app-create-user',
  templateUrl: './create-user.component.html',
  styleUrls: ['./create-user.component.css']
})
export class CreateUserComponent implements OnInit{

  reactiveForm : FormGroup;


 constructor(private http: HttpClient,private router : Router){

 }
 ngOnInit(): void {

     this.reactiveForm = new FormGroup({
      firstName : new FormControl(null,[Validators.required]),
      lastName: new FormControl(null),
      password: new FormControl(null,[Validators.required ,Validators.minLength(8),Validators.maxLength(12)]),
      email:new FormControl(null,[Validators.required,Validators.email])
     })

 }



 public postMethod(){
  let body = {
     "firstName": this.reactiveForm.value.firstName,
    "lastName": this.reactiveForm.value.lastName,
    "email": this.reactiveForm.value.email,
    "password": this.reactiveForm.value.password
  };


  const sessionData = sessionStorage.getItem('key');
    if (sessionData)
     {
      const token = JSON.parse(sessionData).token;

  //const token = JSON.parse(sessionStorage.getItem('key')).token;
  const headers = new HttpHeaders({
    'Authorization': `Bearer ${token}`
  });

   this.http.post("http://localhost:8082/api/manager/new/user",body,{ headers, responseType: 'json' }).subscribe((data) =>{
    sessionStorage.setItem('new-user-Bymanager',JSON.stringify(data));
      console.log(data);
   })
 }
 else{
  this.router.navigateByUrl('/login');
 }
}

OnFormSubmit(){
  this.postMethod();
  console.log(this.reactiveForm);
 }
}
<div class="register-contianer">
    <div class="conatiner2">
    <h3 id="sign-up-board">Create a User</h3>
   <form [formGroup]="reactiveForm"  (ngSubmit)="OnFormSubmit()">
      <div class="register-content">
      <label class="form-elements">
        <input type="text" placeholder="First Name*" formControlName="firstName"/>
        <label class="name-class">
        <input type="text" placeholder="Last Name" formControlName="lastName"/>
        </label>
        <small *ngIf="reactiveForm.get('firstName').invalid&& reactiveForm.get('firstName').touched">
          *FirstName is required filed
        </small>
      </label>

      <label class="form-elements">
        <input type="text" placeholder="email*" size="51px" formControlName="email"/>
        <small *ngIf="reactiveForm.get('email').invalid&& reactiveForm.get('email').touched">
          *email is required filed
        </small>

      </label>



      <label class="form-elements">
        <input type="password" placeholder="password*" id="password" size="51px" formControlName="password"/>
        <small *ngIf="reactiveForm.get('password').invalid&& reactiveForm.get('password').touched">
          *password is required filed
        </small>
      </label>


      <br>
       <p *ngIf="">User Created Successfully</p>
      <br>
      <label>
        <button type="submit" class="form-btn" [disabled]="reactiveForm.invalid">Register</button>
      </label>
     </div>
   </form>
</div>
</div>
