import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { SharedDataService } from '../../shared-data.service';
@Component({
  selector: 'login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  token : any;
  reactiveForm : FormGroup;
  invalidCredential: boolean =false;
  constructor(private http: HttpClient,private router : Router,private sharedDataService: SharedDataService){

  }
  ngOnInit(): void {
     this.reactiveForm = new FormGroup({
       "email": new FormControl(null,[Validators.required,Validators.email]),
       "password": new FormControl(null,[Validators.required,Validators.minLength(8),Validators.maxLength(12)])
     })
  }

  public login()
  { 
    if(this.reactiveForm.valid){
    this.http.post("http://localhost:8082/api/signin", this.reactiveForm.value , {responseType:'json'}).subscribe({
      next : (data: any) => {
        console.log(data);
        this.invalidCredential = false;
        this.token = data.token;
        this.sharedDataService.setToken(this.token);
        // Navigate to the dashboard page
        this.router.navigateByUrl('/dashboard');
      },

      error:(error : any) => {
        this.invalidCredential = true;
      }
    }
    );
  }  
  }

  public ClickOnLogin()
  {  
    this.login();
    console.log(this.reactiveForm);
  }


}
