import { Component,OnInit } from '@angular/core';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
@Component({
  selector: 'app-usertask-update',
  templateUrl: './usertask-update.component.html',
  styleUrls: ['./usertask-update.component.css']
})
export class UsertaskUpdateComponent implements OnInit {
  updateClicked = false;
  taskId:number;
  taskStatus:String;
  constructor(private http: HttpClient){}
  ngOnInit(): void {
  }
  updateTaskStatus()
  {  
 //  this.updateClicked = true;
    //  console.log("i am printing taskId");
    //  console.log(this.taskId);
    //  console.log(this.taskStatus);
     const token = JSON.parse(sessionStorage.getItem('key')).token;
     const headers = new HttpHeaders({
       'Authorization': `Bearer ${token}`
     });

     this.http.put('http://localhost:8083/api/task/update/'+this.taskId ,{ status: this.taskStatus }, { headers,responseType:'text'}).subscribe((response)=>{
      console.log(response);
    })
 }
}
<div>
  <div class="status-form">
  <form (ngSubmit)="updateTaskStatus()">
    <h3>Project Status</h3>
    <div class="form-container">
      <div class="task-id">
      <label for="taskId">Project ID:</label>
      <input type="number" id="taskId" name="taskId" [(ngModel)]="taskId">
    </div>
    <div>
      <label for="status">Status:</label>
      <select id="status" name="status" [(ngModel)]="taskStatus">
        <option value="COMPLETED">COMPLETED</option>
        <option value="IN_PROGRESS">IN PROGRESS</option>
        <option value="ON_HOLD">ON HOLD</option>
      </select>
    </div>
  </div>
    <button type="submit" class="btn-class">Update</button>
  </form>
  </div>
</div>
