import { Component, OnInit } from '@angular/core';
import { HttpClient,HttpHeaders } from '@angular/common/http';
import { SharedDataService } from '../../../shared-data.service';
import {}
@Component({
  selector: 'app-dashboard-main',
  templateUrl: './dashboard-main.component.html',
  styleUrls: ['./dashboard-main.component.css']
})
export class DashboardMainComponent implements OnInit{

  sideBarOpen = true;
  authtoken: any;
  constructor(private http : HttpClient,private sharedDataService: SharedDataService){}
  token: string | null = null;
  ngOnInit(): void {
    this.getTasks();
  }



  public getTasks(){
    const token = this.sharedDataService.getToken();
    if (!token) {
      throw new Error('Token not found');
    }

    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });
      this.http.get('http://localhost:8083/api/project/1',{headers}).subscribe((data) =>{
        console.log(data);
        this.sharedDataService.setProjectDeatils(data);
       // this.sharedDataService.setProjectName(data.P)
      })
  }

  public sideBarToggler()
  {
    this.sideBarOpen = !this.sideBarOpen;
  }

}
