import { Component } from '@angular/core';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
@Component({
  selector: 'app-manager-projects',
  templateUrl: './manager-projects.component.html',
  styleUrls: ['./manager-projects.component.css']
})
export class ManagerProjectsComponent {

  projects = []; // Populate this array with your project data

  selectedProject: any;

  constructor(private http : HttpClient) {}

  selectProject(project: any) {
    this.selectedProject = project;
  }

  public GetProject(){

    const token = JSON.parse(sessionStorage.getItem('key')).token;
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });
    this.http.get('http://localhost:8083/api/project/details',{headers,responseType:'json'}).subscribe((response)=>{
      console.log(response);
      sessionStorage.setItem('projectkey',JSON.stringify(response));
    })
  }
}
<div class="project-list">
    <h2>Projects</h2>
    <ul>
      <li *ngFor="let project of projects" (click)="selectProject(project)">
        {{ project.name }}
      </li>
    </ul>
  </div>

  <div class="project-details" *ngIf="selectedProject">
    <h2>Project Details</h2>
    <table>
      <tr>
        <th>Project ID</th>
        <th>Name</th>
        <th>Description</th>
        <th>Status</th>

      </tr>
      <tr>
        <td>{{ selectedProject.project_id }}</td>
        <td>{{ selectedProject.name }}</td>
        <td>{{ selectedProject.description }}</td>
        <td>{{ selectedProject.status }}</td>

      </tr>
    </table>

    <h2>Tasks</h2>
    <table>
      <tr>
        <th>Task ID</th>
        <th>Name</th>
        <th>Status</th>

      </tr>
      <tr *ngFor="let task of selectedProject.tasks">
        <td>{{ task.task_id }}</td>
        <td>{{ task.name }}</td>
        <td>{{ task.status }}</td>

      </tr>
    </table>
  </div>
