import { Component,OnInit} from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
import { Router } from '@angular/router';
@Component({
  selector: 'app-update-role',
  templateUrl: './update-role.component.html',
  styleUrls: ['./update-role.component.css']
})
export class UpdateRoleComponent implements OnInit {
  currentRole:any;
  showMessage: boolean = false;
  constructor(private formBuilder: FormBuilder,private http : HttpClient,private router :Router) { }
  registerForm: FormGroup;
  ngOnInit(): void {
    this.registerForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      role: ['', Validators.required]
    });
  }

  get f() { return this.registerForm.controls; }

  onSubmit() {
    if (this.registerForm.invalid) {
      return;
    }

    // Submit the form data
    console.log(this.registerForm.value);
    console.log(this.registerForm.value.role);
  }
}
<div>
    <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
<div>
    <label>
    <input type="email" formControlName="email" placeholder="Email">
    </label>
    <small *ngIf="f['email'].invalid && (f['email'].dirty || f['email'].touched)" class="error-message">
      <small *ngIf="f['email'].invalid">Email is required</small>
    </small>
  </div>
   <div>
    <label>Role:</label>
    <select formControlName="role">
      <option value="">Select Role</option>
      <option value="admin">ADMIN</option>
      <option value="manager">Manager</option>
      <option value="user">USER</option>
    </select>
    <small *ngIf="f['role'].invalid && (f['role'].dirty || f['role'].touched)">
      <small *ngIf="f['role'].invalid">Role is required</small>
    </small>
   </div>
   </form>
</div>
