import { Component } from '@angular/core';
import { faListCheck,faUser,faHand,faLocationDot } from '@fortawesome/free-solid-svg-icons';
@Component({
  selector: 'app-sidenav',
  templateUrl: './sidenav.component.html',
  styleUrls: ['./sidenav.component.css']
})
export class SidenavComponent {

  faUser= faUser;
  tasks=faListCheck;
  help=faHand;
  location=faLocationDot;
}
<div>

    <div class="company-logo">
      <img src="../../assets/image-accordion.png"  alt="company logo is missing" width="200px" height="100px">
    </div>
    <mat-divider></mat-divider>
    <div class="nav-items">
    <div class="nav-item">
        <fa-icon [icon]="faUser"></fa-icon>
        <a routerLink="../dashboard/profile">Profile</a></div>
    <div class="nav-item">
        <fa-icon [icon]="tasks"></fa-icon>
        <a routerLink="../dashboaprojects">Projects</a></div>
    <div class="nav-item">
        <fa-icon [icon]="tasks"></fa-icon>
        <a routerLink="../tasks">Task</a></div>
    <div class="nav-item">
        <fa-icon [icon]="location"></fa-icon>
        <a routerLink="www.google.com"> Location</a></div>
  </div>
  </div>
