import { Component, OnInit } from '@angular/core';
import { NgModule } from '@angular/core';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
@Component({
  selector: 'app-manager-projects',
  templateUrl: './manager-projects.component.html',
  styleUrls: ['./manager-projects.component.css']
})
export class ManagerProjectsComponent implements OnInit{

  projects: any[] = []; // Populate this array with your project data
  updateClicked = false;
  selectedProject: any;
  selectedTaskStatus: string;

  constructor(private http : HttpClient) {}
  ngOnInit(): void {
    this.projects =  JSON.parse(sessionStorage.getItem('projectkey'));
  }
  selectProject(project: any) {
    this.selectedProject = project;
  }

  updateTaskStatus()
   {  
    this.updateClicked = true;
    if (this.selectedProject && this.selectedTaskStatus) {
      const token = JSON.parse(sessionStorage.getItem('key')).token;
      const headers = new HttpHeaders({
        'Authorization': `Bearer ${token}`
      });

      // Iterate over tasks to update status
      this.selectedProject.tasks.forEach(task => {
        // Make HTTP POST request to update task status
        this.http.post<any>(`http://localhost:8083/api/task/status/${task.task_id}`, { status: this.selectedTaskStatus }, { headers })
          .subscribe(
            (response) => {
              console.log('Task status updated successfully:', response);
              // Handle response if needed
            },
            (error) => {
              console.error('Error updating task status:', error);
              // Handle error if needed
            }
          );
      });
    } else {
      console.error('No project selected or task status not selected.');
      // Handle case where no project is selected or task status is not selected
    }
  }
}
<div class="container-fluid page">
  <div class="d-flex page__box p-3 mt-2">Projects
    <button type="button" class="btn btn-primary" (click)="updateTaskStatus()">Update</button>
  </div>
  <div class="page__content shadow p-3 position-relative">
    <div class="page__content-data">
<div class="project-list">

    <ul>
      <li *ngFor="let project of projects" (click)="selectProject(project)">
        {{ project.name }}
      </li>
    </ul>
  </div>

  <div class="project-details" *ngIf="selectedProject">
    <h2>Project Details</h2>
    <table>
      <tr>
        <th>Project ID</th>
        <th>Name</th>
        <th>Description</th>
        <th>Status</th>
      </tr>
      <tr>
        <td>{{ selectedProject.project_id }}</td>
        <td>{{ selectedProject.name }}</td>
        <td>{{ selectedProject.description }}</td>
        <td>{{ selectedProject.status }}</td>
      </tr>
    </table>

    <h2>Tasks</h2>
    <table>
      <tr>
        <th>Task ID</th>
        <th>Name</th>
        <th>Status</th>
      </tr>
      <tr *ngFor="let task of selectedProject.tasks">
        <td>{{ task.task_id }}</td>
        <td>{{ task.name }}</td>
        <td>
          <select [(ngModel)]="selectedTaskStatus" [disabled]="!updateClicked">
            <option value="COMPLETED" [selected]="task.status === 'COMPLETED'">COMPLETED</option>
            <option value="IN_PROGRESS" [selected]="task.status === 'IN_PROGRESS'">IN PROGRESS</option>
            <option value="ON_HOLD" [selected]="task.status === 'ON_HOLD'">ON HOLD</option>
          </select>
        </td>
      </tr>
    </table>
  </div>
    </div>
  </div>
</div>
