import { Component } from '@angular/core';
import { Chart } from 'chart.js';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
@Component({
  selector: 'app-default-dashboard',
  templateUrl: './default-dashboard.component.html',
  styleUrls: ['./default-dashboard.component.css']
})
export class DefaultDashboardComponent implements OnI {
  chartdata : any ;
  labeldata: any[]=[];
  taskCounts: any = {};
  projectCounts : any = {};

  chartLabels = [];
  chartData = [];
  constructor(private http:HttpClient){}
  ngOnInit(): void {
    // Retrieve token from sessionStorage
    const token = JSON.parse(sessionStorage.getItem('key')).token;

    // Set Authorization header with token
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });

    // Make HTTP GET request to fetch project details
    this.http.get<any[]>('http://localhost:8083/api/project/details', { headers, responseType: 'json' }).subscribe(
      (response) => {
        console.log(response);

        // Store project data in sessionStorage
        sessionStorage.setItem('projectkey', JSON.stringify(response));

        // Initialize projectCounts object to store counts by status
        this.projectCounts = {
          COMPLETED: 0,
          IN_PROGRESS: 0,
          ON_HOLD: 0
        };

        // Loop through each project in the response
        response.forEach(project => {
          // Increment the count for the corresponding status
          if (project.status in this.projectCounts) {
            this.projectCounts[project.status]++;
          }
        });

        // Initialize empty arrays to store chart labels and data
        this.chartLabels = [];
        this.chartData = [];

        // Loop through each status in the projectCounts object
        Object.keys(this.projectCounts).forEach(status => {
          // Push the status (e.g., COMPLETED, IN_PROGRESS, ON_HOLD) to chartLabels array
          this.chartLabels.push(status);

          // Push the count of projects with this status to chartData array
          this.chartData.push(this.projectCounts[status]);
