    </mat-drawer>
    <mat-drawer-content>
      <app-manager-header (toggleSidebarForMe)="sideBarToggler()"></app-manager-header>
      <router-outlet></router-outlet>
    </mat-drawer-content>
  </mat-drawer-container>
