import { Component } from '@angular/core';
import { SharedDataService } from '../../../shared-data.service';
@Component({
  selector: 'app-projects',
  templateUrl: './projects.component.html',
  styleUrls: ['./projects.component.css']
})
export class ProjectsComponent {
  constructor(private sharedData : SharedDataService){}
  projectData: any;
  ngOnInit(): void {
      this.projectData = this.sharedData.getProjectDeatils();
  }
}
<div class="container-fluid page">
    <div class="d-flex page__box p-3 mt-2">Projects</div>
    <div class="page__content shadow p-3 position-relative">
      <div class="page__content-data position-absolute text-center">
        <div class="main-data">
        <div>Project ID: {{ projectData.project_id }}</div>
        <div>Project Name: {{ projectData.name }}</div>
        <div>Description: {{ projectData.description }}</div>
        <div>Status: {{ projectData.status }}</div>
        <div>Department Name: {{ projectData.department_name }}</div>
        </div>
      </div>
    </div>
  </div>
