  <div class="page__content shadow p-3 position-relative">
    <div class="page__content-data">
<div class="container">
<form [formGroup]="projectForm" (ngSubmit)="$event.preventDefault(); onSubmit()">
    <div class="task-container">
    <h2>Create Project</h2>
    <div class="form-group">
      <label for="DepartmentName">Department Name</label>
      <select id="DepartmentName" formControlName="departmentName">
        <option  value="Engineering">Engineering</option>
        <option  value="IT Services">IT Services</option>
        <option  value="HR">HR</option>
        <option  value="Consulting">Consulting</option>
        <option  value="Marketing">Marketing</option>
      </select>
      <label for="name">Name:</label>
      <input type="text" id="name" formControlName="name">

      <label for="status">Status:</label>
      <select id="status" formControlName="status">
        <option  value="ON_HOLD">ON_HOLD</option>
        <option  value="IN_PROGRESS">IN_PROGRESS</option>
        <option  value="completed">COMPLETED</option>
      </select>
      <label for="assignedTo">Assigned To:</label>
      <select id="assignedTo" formControlName="assignedTo">
        <option value="" disabled>Select Manager</option>
        <option *ngFor="let manager of allManagers" [value]="manager.email">{{ manager.email }}</option>
      </select>
    </div>

    <div class="form-group">
      <label for="assignDate">Assign Date:</label>
      <input type="date" id="assignDate" formControlName="assignDate">
      <label for="completionDate">Completion Date:</label>
      <input type="date" id="completionDate" formControlName="completionDate">
      <label for="description">Description</label>
      <input type="text" id="description" formControlName="description">
    </div>

    <p *ngIf="isCreated"> Project is created successfully</p>
    <button type="submit" >Submit</button>
  </div>
  </form>
</div>

<div class="container">
<form [formGroup]="updateprojectForm" (ngSubmit)="$event.preventDefault(); UpdateProject()">
    <div class="task-container">
    <h2>Update Project</h2>
    <div class="form-group">
      <label for="DepartmentName">Department Name</label>
      <select id="DepartmentName" formControlName="departmentName">
        <option  value="Engineering">Engineering</option>
        <option  value="IT Services">IT Services</option>
        <option  value="HR">HR</option>
        <option  value="Consulting">Consulting</option>
        <option  value="Marketing">Marketing</option>
      </select>
      <label for="name">Name:</label>
      <input type="text" id="name" formControlName="name">

      <label for="status">Status:</label>
      <select id="status" formControlName="status">
        <option  value="ON_HOLD">ON_HOLD</option>
        <option  value="IN_PROGRESS">IN_PROGRESS</option>
        <option  value="COMPLETED">COMPLETED</option>
      </select>
      <label for="assignedTo">Assigned To:</label>
      <select id="assignedTo" formControlName="assignedTo">
        <option value="" disabled>Select Manager</option>
        <option *ngFor="let manager of allManagers" [value]="manager.email">{{ manager.email }}</option>
      </select>

    </div>

    <div class="form-group">
      <label for="assignDate">Assign Date:</label>
      <input type="date" id="assignDate" formControlName="assignDate">
      <label for="completionDate">Completion Date:</label>
      <input type="date" id="completionDate" formControlName="completionDate">
      <label for="description">Description</label>
      <input type="text" id="description" formControlName="description">
      <label>Enter Project Id</label>
      <select id="projectId" name="projectId"  formControlName="projectId">
      <option value="" disabled>Select Project</option>
      <option *ngFor="let project of projects" [value]="project.project_id">{{ project.project_id }}</option>
    </select>
    </div>

    <p *ngIf="isUpdated"> Project is Updated successfully</p>
    <button type="submit" >confirm</button>
  </div>
  </form>
</div>
