 }
}
<div>
  <div class="status-form">
  <form (ngSubmit)="updateTaskStatus()">
    <h3>Project Status</h3>
    <div class="form-container">
      <div class="task-id">
      <label for="taskId">Project ID:</label>
      <input type="number" id="taskId" name="taskId" [(ngModel)]="tasktId">
    </div>
    <div>
      <label for="status">Status:</label>
      <select id="status" name="status" [(ngModel)]="taskStatus">
        <option value="COMPLETED">COMPLETED</option>
        <option value="IN_PROGRESS">IN PROGRESS</option>
        <option value="ON_HOLD">ON HOLD</option>
      </select>
    </div>
  </div>
    <button type="submit" class="btn-class">Update</button>
  </form>
  </div>
</div>
