import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
import { Router } from '@angular/router';
@Component({
  selector: 'app-delete-user',
  templateUrl: './delete-user.component.html',
  styleUrls: ['./delete-user.component.css']
})
export class DeleteUserComponent implements OnInit {

  userId:any;
  registerForm:FormGroup;
  deleteUserStatus:boolean;
  projectId:number;
  allManagers:any[]=[];
  allUsers:any[]=[];
  length:number;
  constructor(private formBuilder: FormBuilder,private http : HttpClient,private router :Router) { }
  ngOnInit(): void {


      const token = JSON.parse(sessionStorage.getItem('key')).token
      const headers = new HttpHeaders({
        'Authorization': `Bearer ${token}`
      });

      this.http.get('http://localhost:8082/api/admin/all', { headers, responseType: 'json' }).subscribe(
       {next: 
        (data: any) => {
          this.allUsers=data;
          this.allManagers.push(...data.filter(item => item.role === 'Manager'));
          // console.log(data);
          // this.length = data.length;
          // console.log(this.allUsers);
          // console.log(this.length);    
        },
        error:
        (error) => {
          console.error('Error fetching data:', error);
        }
       } );

  }

  public DeleteUser(){
    const token = JSON.parse(sessionStorage.getItem('key')).token
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });
      this.http.delete('http://localhost:8082/api/admin/delete/'+this.userId,{headers,responseType:'text'}).subscribe((data)=>{
        console.log(data);
      })
  }

  public DeleteProject(){
    const token = JSON.parse(sessionStorage.getItem('key')).token
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });
    this.http.delete('http://localhost:8083/api/project/delete/'+this.projectId,{headers,responseType:'text'}).subscribe((data)=>{
      console.log(data);
    })

  }
}
<div class="container-fluid page">
    <div class="d-flex page__box p-3 mt-2">Delete</div>
    <div class="page__content shadow p-3 position-relative">
      <div class="page__content-data">
<form (ngSubmit)="DeleteUser()">
    <div class="user">
    <h1>Delete User</h1>
    <label>Enter User Id</label>
    <input type="number"  id="userId" name="userId" [(ngModel)]="userId">
    <br>
    <button class="user-button" type="submit">Confirm</button>
    </div>
</form>


<form (ngSubmit)="DeleteProject()">
    <div class="user">
      <h1>Delete Project</h1>
      <label>Select Project Manager:</label>
      <select id="managerId" name="managerId" [(ngModel)]="selectedManagerId">
        <option *ngFor="let manager of managers" [value]="manager.id">{{ manager.email }}</option>
      </select>
      <br>
      <button class="user-button" type="submit">Confirm</button>
    </div>
  </form>



</div>
</div>
</div>
