import { Injectable } from '@angular/core';
import {
  HttpRequest,
  HttpHandler,
  HttpEvent,
  HttpInterceptor
} from '@angular/common/http';
import { Observable } from 'rxjs';
import { SharedDataService } from '../shared-data.service';
@Injectable()
export class MasterInterceptor implements HttpInterceptor {

  constructor(private sharedData :SharedDataService) {}
  token : any;
  intercept(request: HttpRequest<unknown>, next: HttpHandler): Observable<HttpEvent<unknown>> {

    const allowRequest1="http://localhost:8082/api/signup";
    const allowRequest2="http://localhost:8082/api/signin";
    if(request.url==allowRequest1 || request.url==allowRequest2){
      return next.handle(request);
    }
    else{
    //   let loggedUserData:any;
    // let localdata=sessionStorage.getItem('key');
    // if(localdata!=null){
    //   loggedUserData=JSON.parse(localdata);
    // }
    const sessionData = sessionStorage.getItem('key');
    if(sessionData)
    this.token = JSON.parse(sessionStorage.getItem('key')).token
    const cloneRequest=request.clone({
      setHeaders:{
        Authorization:`Bearer ${this.token}`
      }
    })
    return next.handle(cloneRequest);
    }

  }
}
