import { Component, OnInit } from '@angular/core';
import { SharedDataService } from '../../../shared-data.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
import { Router } from '@angular/router';
@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit{

  constructor(private sharedData : SharedDataService,private formBuilder: FormBuilder,private http : HttpClient){}
  profileForm: FormGroup;
  profileData : any; 
  isExist: boolean;
  ngOnInit(): void {
    this.profileForm = this.formBuilder.group({
      firstName: ['', Validators.required],
      lastName: [{ value: '', disabled: true }],
      email: [{ value: '', disabled: true }, [Validators.required, Validators.email]],
      address: [''],
      department: [{ value: '', disabled: true }, [Validators.required]],
      gender: [{ value: '', disabled: true }],
      skills: [''],
      phoneNumber: [{ value: '', disabled: true }]
    });
   this.getProfile();
  }

  get f() { return this.profileForm.controls; }

  onSubmit() {
    if (this.profileForm.invalid) {
      return;
    }

    console.log(this.profileForm.value);
  }

  public getProfile(){
    this.http.get('http://localhost:8081/api/user/12').subscribe((data)=>{
      console.log(data);
      this.profileData = data;
      this.formData.departmentPlaceholder = this.profileData.department_name;
      this.formData.firstNamePlaceholder = this.sharedData.getFirstName();
      this.formData.lastNamePlaceholder = this.sharedData.getLastName();
      console.log(this.sharedData.getFirstName());
      this.formData.emailPlaceholder = this.sharedData.getEmailDetails();
      this.formData.phoneNumberPlaceholder = this.profileData.phone_no;

    })
  }
  formData = {
    firstNamePlaceholder: '',
    lastNamePlaceholder: '',
    emailPlaceholder: '',
    phoneNumberPlaceholder: '',
    departmentPlaceholder: '',

  };

}
<div *ngIf="isExist; else showProfile"></div>

<div class="container" >
  <form [formGroup]="profileForm" (ngSubmit)="onSubmit()">
    <div class="form-container">
     <div class="nam">
      <div class="name-content">
        <label>FirstName</label>
        <input type="text" formControlName="firstName" [value]="formData.firstNamePlaceholder">
      </div> 


      <div class="name-content">
        <label>LastName:</label>
        <input type="text" formControlName="lastName" [value]="formData.lastNamePlaceholder">
      </div>
      </div>

      <div class="nam">
      <div class="name-content2">
      <label>Email:</label>
        <input type="email" formControlName="email" [value]="formData.emailPlaceholder">
      </div>

      <div class="name-content1">
        <label for="department">Department:</label>
        <input type="text" id="department" formControlName="department" [value]="formData.departmentPlaceholder">
      </div>
      </div>



      <div class="name-content">
      <label for="phoneNumber">Phone Number:</label>
      <input type="text" id="phoneNumber" formControlName="phoneNumber" [value] ="formData.phoneNumberPlaceholder">
      </div>


        <div class="gender-radio">
          <div class="radio-options">
            <label><input type="radio" formControlName="gender" value="male">Male</label>
            <label><input type="radio" formControlName="gender" value="female">Female</label>
            <label><input type="radio" formControlName="gender" value="other">Other</label>
          </div>
        </div>

        <div class="skill-content">
        <label for="skills">Skills:</label>
        <textarea id="skills" formControlName="skills" placeholder="Enter your skills here"></textarea>
        </div>

    <!-- <button type="submit" [disabled]="profileForm.invalid">Save</button> -->
    </div>
  </form>
</div>

<div  class="container" #showProfile>
  <form [formGroup]="profileForm" (ngSubmit)="onSubmit()">
    <div class="form-container">
     <div class="nam">
      <div class="name-content">
        <label>FirstName</label>
        <input type="text" formControlName="firstName" [value]="formData.firstNamePlaceholder">
      </div> 


      <div class="name-content">
        <label>LastName:</label>
        <input type="text" formControlName="lastName" [value]="formData.lastNamePlaceholder">
      </div>
      </div>

      <div class="nam">
      <div class="name-content2">
      <label>Email:</label>
        <input type="email" formControlName="email" [value]="formData.emailPlaceholder">
      </div>

      <div class="name-content1">
        <label for="department">Department:</label>
        <input type="text" id="department" formControlName="department" [value]="formData.departmentPlaceholder">
      </div>
      </div>



      <div class="name-content">
      <label for="phoneNumber">Phone Number:</label>
      <input type="text" id="phoneNumber" formControlName="phoneNumber" [value] ="formData.phoneNumberPlaceholder">
      </div>


        <div class="gender-radio">
          <div class="radio-options">
            <label><input type="radio" formControlName="gender" value="male">Male</label>
            <label><input type="radio" formControlName="gender" value="female">Female</label>
            <label><input type="radio" formControlName="gender" value="other">Other</label>
          </div>
        </div>

        <div class="skill-content">
        <label for="skills">Skills:</label>
        <textarea id="skills" formControlName="skills" placeholder="Enter your skills here"></textarea>
        </div>

    <!-- <button type="submit" [disabled]="profileForm.invalid">Save</button> -->
    </div>
  </form>
</div>
