import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
import { Router } from '@angular/router';
@Component({
  selector: 'app-delete-user',
  templateUrl: './delete-user.component.html',
  styleUrls: ['./delete-user.component.css']
})
export class DeleteUserComponent implements OnInit {
  //http://localhost:8082/api/admin/delete/8
  userId:any;
  registerForm:FormGroup;
  deleteUserStatus:
  constructor(private formBuilder: FormBuilder,private http : HttpClient,private router :Router) { }
  ngOnInit(): void {

  }

  public DeleteUser(){
    const token = JSON.parse(sessionStorage.getItem('key')).token
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });
      this.http.delete('http://localhost:8082/api/admin/delete/'+this.userId,{headers,responseType:'text'}).subscribe((data)=>{
        console.log(data);
      })
  }

}
<div class="container-fluid page">
    <div class="d-flex page__box p-3 mt-2">Delete User</div>
    <div class="page__content shadow p-3 position-relative">
      <div class="page__content-data">
<form (ngSubmit)="DeleteUser()">
    <div class="user">
    <h1>Delete User</h1>
    <label>Enter User Id</label>
    <input type="number"  id="userId" name="userId" [(ngModel)]="userId">
    <br>
    <button class="user-button" type="submit">Confirm</button>
    </div>
</form>
