import { Component } from '@angular/core';
import { SharedDataService } from '../../../shared-data.service';
@Component({
  selector: 'app-tasks',
  templateUrl: './tasks.component.html',
  styleUrls: ['./tasks.component.css']
})
export class TasksComponent {
  tasksLength : any ;
  constructor(private sharedData : SharedDataService){}
  projectData: any;
  ngOnInit(): void {
      this.projectData = this.sharedData.getProjectDeatils();
      this.tasksLength = this.projectData.tasks.length;
  }
  public sideBarToggler()
  {
    this.sideBarOpen = !this.sideBarOpen;
  }
}
<mat-drawer mode="side" [opened]="sideBarOpen">
    <app-sidenav></app-sidenav>
  </mat-drawer>
  <app-header (toggleSidebarForMe)="sideBarToggler()"></app-header>
<div class="container-fluid page">
    <div class="d-flex page__box p-3 mt-2">Tasks</div>
    <div class="page__content shadow p-3 position-relative">
      <div class="page__content-data position-absolute text-center">
        <div class="main-data">
        <table>
            <thead>
                <tr> 
                    <th>Task ID</th>
                    <th>Project Id</th>
                    <th>Task Name</th>
                    <th>Status</th>
                    <th>AssignedBy</th>
                </tr>
            </thead>

            <tbody *ngFor ="let _ of [].constructor(tasksLength); let i = index">
                <tr>
                    <th>{{ projectData.tasks[0].task_id}}</th>
                    <th>{{ projectData.project_id}}</th>
                    <th>{{ projectData.tasks[0].name}}</th>
                    <th>{{ projectData.tasks[0].status}}</th>
                    <th>{{ projectData.tasks[0].assignedBy}}</th>
                </tr>
            </tbody>
        </table>
        </div>
      </div>
    </div>
  </div>
