import { Component, OnInit} from '@angular/core';
import { HttpClient,HttpHeaders } from '@angular/common/http';
import { SharedDataService } from '../../../shared-data.service';
import { Router, RouterModule, Routes } from '@angular/router';
import { preRouteGuard } from '../../../pre-route.guard';
import { saveAs } from 'file-saver';
import { DataService } from '../../../data.service';
@Component({
  selector: 'app-dashboard-main',
  templateUrl: './dashboard-main.component.html',
  styleUrls: ['./dashboard-main.component.css']
})
export class DashboardMainComponent implements OnInit{

  sideBarOpen = true;
  authtoken: any;
  constructor(private http : HttpClient,private sharedDataService: SharedDataService,private router : Router){}
  token: string | null = null;
  ngOnInit(): void {
    this.Data.fetchDataAndSaveToFile();
  }



  public getTasks(){
    const token = this.sharedDataService.getToken();
    if (!token) {
      this.router.navigateByUrl('/login')
    }

    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });
      this.http.get('http://localhost:8083/api/project/1',{headers,responseType:'json'}).subscribe((data) =>{
        console.log(data);

        const jsonData = JSON.stringify(data);
        const jsonBlob = new Blob([jsonData], { type: 'application/json' });
        saveAs(jsonBlob, 'projectdata.json');
        this.sharedDataService.setProjectDeatils(data);
      })
  }

  public sideBarToggler()
  {
    this.sideBarOpen = !this.sideBarOpen;
  }

}
