import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
import { Router } from '@angular/router';
@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit{

  mySet: Set<any>;
  constructor(private formBuilder: FormBuilder,private http : HttpClient){
    this.mySet = new Set();
  }
  profileForm: FormGroup;
  profileData : any; 
  isExist: boolean;
  id:number;
  skillIdArray:any;
  newSkill:string;
  userEmail:string;

  //{ value: '', disabled: true }
  ngOnInit(): void {

    this.profileForm = this.formBuilder.group({
      firstName: [''],
      lastName: [''],
      email: ['', [Validators.required, Validators.email]],
      address: [''],
      department: ['', [Validators.required]],
      gender: [],
      skillname: [''],
      phoneNumber: []
    });

    this.getProfile();
  }



  onSubmit() {
    console.log("clicked");
    console.log(this.profileForm);
    console.log(JSON.parse(sessionStorage.getItem('key')).user_id);
    console.log(this.profileForm.value.department);
    this.postProfile();

  }

  public getProfile(){

    this.id = JSON.parse(sessionStorage.getItem('key')).user_id;
    console.log(this.id);
    this.http.get('http://localhost:8081/api/user/' +this.id)
    .subscribe((data) => {
      console.log(data);
      this.profileData = data;
      this.formData.departmentPlaceholder = this.profileData.department_name;
      this.formData.firstNamePlaceholder = JSON.parse(sessionStorage.getItem('key')).firstName;
      this.formData.lastNamePlaceholder = JSON.parse(sessionStorage.getItem('key')).lastName;
      this.formData.emailPlaceholder = JSON.parse(sessionStorage.getItem('key')).email
      this.formData.phoneNumberPlaceholder = this.profileData.phone_no;
    },(error) =>{
         console.log(error);
         this.isExist=false;
    });
  }
  formData = {
    firstNamePlaceholder: '',
    lastNamePlaceholder: '',
    emailPlaceholder: JSON.parse(sessionStorage.getItem('key')).email,
    phoneNumberPlaceholder: '',
    departmentPlaceholder: '',

  };


  public postProfile(){
    console.log("on click on skill this function")
    let body = {
      "user_id":JSON.parse(sessionStorage.getItem('key')).user_id,
     "department_name":this.profileForm.value.department,
     "phone_no":this.profileForm.value.phoneNumber,
      "gender": this.profileForm.value.gender,
      "address":this.profileForm.value.address,
      "skillId": this.skillIdArray
   };
   console.log(body);
    this.http.post('http://localhost:8081/api/user/profile',body).subscribe((data)=>{
      console.log(data);
      this.isExist= true;
      this.getProfile();
    },
    (error) =>{
      console.log(error);
      this.isExist=true;
    });


  }

  addSkill() {
     let skillbody ={
          "name": this.profileForm.value.skillname
     };
     this.http.post('http://localhost:8081/api/skill/add',skillbody).subscribe((data)=>{
      console.log(data);
      sessionStorage.setItem('skillkey',JSON.stringify(data));
      this.mySet.add( JSON.parse(sessionStorage.getItem('skillkey')).id);
      this.skillIdArray = Array.from(this.mySet);
     })
     console.log("the skill is added");
  }

}
