import { Component,OnInit} from '@angular/core';
import { FormBuilder,  FormGroup,  Validators } from '@angular/forms';
import { HttpClient,HttpHeaders } from '@angular/common/http';
@Component({
  selector: 'app-update-task',
  templateUrl: './update-task.component.html',
  styleUrls: ['./update-task.component.css']
})
export class UpdateTaskComponent implements OnInit{

  projects: any[] = []; // Populate this array with your project data
  updateClicked = false;
  taskId:number;
  projectId:number;
  taskStatus:string;
  taskForm: FormGroup;
  isCreated= false;
  projectStatus:string;
  constructor(private fb: FormBuilder,private http:HttpClient) {}

  ngOnInit(): void {
    this.taskForm = this.fb.group({
      name: ['' ],
      status: [''],
      assignDate: [''],
      completionDate: [''],
      assignedTo: [''],
      projectId: [''],
      taskId:['']
    });
    this.isCreated=false;
  }
  updateProjectStatus()
  {  
 //  this.updateClicked = true;
     console.log("i am printing taskId");
     console.log(this.taskId);
     console.log(this.taskStatus);
     const token = JSON.parse(sessionStorage.getItem('key')).token;
     const headers = new HttpHeaders({
       'Authorization': `Bearer ${token}`
     });

     this.http.put('http://localhost:8083/api/project/status/'+this.projectId ,{ status: this.projectStatus }, { headers,responseType:'text'}).subscribe((response)=>{
      console.log(response);
    })
 }


 onSubmit() {
  // Handle form submission
  const token = JSON.parse(sessionStorage.getItem('key')).token;
  const email = JSON.parse(sessionStorage.getItem('key')).email;

  let body={
  "name": this.taskForm.value.name,
  "status": this.taskForm.value.status,
  "assignedDate": this.taskForm.value.assignDate,
  "completionDate": this.taskForm.value.completionDate,
  "assignedTo": this.taskForm.value.assignedTo,
  "assignedBy": email,
  "projectId": this.taskForm.value.projectId,
  };

  this.taskId = this.taskForm.value.taskId;

  const headers = new HttpHeaders({
    'Authorization': `Bearer ${token}`
  });
  this.http.post('http://localhost:8083/api/task/update/'+this.taskId,body, { headers, responseType: 'json' }).subscribe((response)=>{
    console.log(response);
    this.isCreated = true;
    setTimeout(() => {
      this.isCreated = false;
    }, 3000); 

  })
  console.log(this.taskForm.value);
}
}
<div class="container-fluid page">
  <div class="d-flex page__box p-3 mt-2">Update </div>
  <div class="page__content shadow p-3 position-relative">
    <div class="page__content-data">
<div class="update-task">
  <div class="container">
    <form [formGroup]="taskForm" (ngSubmit)="onSubmit()">
        <div class="task-container">
        <h2>Update Task</h2>
        <div class="form-group">
                <label for="projectId">Project ID:</label>
                <input type="number" id="projectId" formControlName="projectId">
          <label for="name">Name:</label>
          <input type="text" id="name" formControlName="name">
        </div>

        <div class="form-group1">
          <label for="status">Status:</label>
          <select id="status" formControlName="status">
            <option  value="ON_HOLD">ON_HOLD</option>
            <option  value="IN_PROGRESS">IN_PROGRESS</option>
            <option  value="completed">COMPLETED</option>
          </select>
          <label for="assignedTo">Assigned To:</label>
          <input type="text" id="assignedTo" formControlName="assignedTo">
        </div>


        <div class="form-group">
          <label for="assignDate">Assign Date:</label>
          <input type="date" id="assignDate" formControlName="assignDate">
          <label for="completionDate">Completion Date:</label>
          <input type="date" id="completionDate" formControlName="completionDate">
        </div>

        <div class="form-group">
          <label for="taskId">task ID:</label>
          <input type="number" id="taskId" formControlName="taskId">
          </div>
        <button type="submit" >Submit</button>
        <p *ngIf="isCreated">Task is Updated successfully</p>
      </div>
      </form>
    </div>

    <div>
      <div class="status-form">
      <form (ngSubmit)="updateProjectStatus()">
        <h3>Project Status</h3>
        <div class="form-container">
          <div class="task-id">
          <label for="taskId">Project ID:</label>
          <input type="number" id="taskId" name="taskId" [(ngModel)]="projectId">
        </div>
        <div>
          <label for="status">Status:</label>
          <select id="status" name="status" [(ngModel)]="projectStatus">
            <option value="COMPLETED">COMPLETED</option>
            <option value="IN_PROGRESS">IN PROGRESS</option>
            <option value="ON_HOLD">ON HOLD</option>
          </select>
        </div>
      </div>
        <button type="submit" class="btn-class">Update</button>
      </form>
      </div>
    </div>

    </div>
