import { Component,OnInit  } from '@angular/core';
import { FormBuilder,  FormGroup,  Validators } from '@angular/forms';
import { HttpClient,HttpHeaders } from '@angular/common/http';

@Component({
  selector: 'app-createtask',
  templateUrl: './createtask.component.html',
  styleUrls: ['./createtask.component.css']
})
export class CreatetaskComponent implements OnInit {

  taskForm: FormGroup;
  isCreated= false;
  constructor(private fb: FormBuilder,private http:HttpClient) {}
  projects:any[]=[];
  allUsers:any[]=[];
  allEmployee:any[]=[];
  selectedProjectId: number;
  ngOnInit(): void {
    const token = JSON.parse(sessionStorage.getItem('key')).token;
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });
    this.taskForm = this.fb.group({
      name: ['' ],
      status: [''],
      assignDate: [''],
      completionDate: [''],
      assignedTo: [''],
      projectId: ['']
    });
    this.isCreated=false;


      this.http.get('http://localhost:8082/api/admin/all', { headers, responseType: 'json' }).subscribe(
       {next: 
        (data: any) => {
          this.allEmployee=data;
          for (let i = 0; i < this.allEmployee.length; i++) {
            if(this.allEmployee[i].authority == "USER"){
                 this.allUsers.push(this.allUsers[i]);
            }
         }
        },
        error:
        (error) => {
          console.error('Error fetching data:', error);
        }
       } );


    this.http.get<any[]>('http://localhost:8083/api/project/all/projects',{headers}).subscribe((response)=>{
        console.log(response);
        this.projects = response;
        //this.projectslength = response.length;
      })
  }

  onSubmit() {
    // Handle form submission
    const token = JSON.parse(sessionStorage.getItem('key')).token;
    const email = JSON.parse(sessionStorage.getItem('key')).email;

    let body={
    "name": this.taskForm.value.name,
    "status": this.taskForm.value.status,
    "assignedDate": this.taskForm.value.assignDate,
    "completionDate": this.taskForm.value.completionDate,
    "assignedTo": this.taskForm.value.assignedTo,
    "assignedBy": email,
    "projectId": this.taskForm.value.projectId
    };


    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });
    this.http.post('http://localhost:8083/api/task/create',body, { headers, responseType: 'json' }).subscribe((response)=>{
      console.log(response);
      this.isCreated = true;
      this.isCreated=true;
      setTimeout(() => {
        this.isCreated = false;
      }, 3000); 

    })
    console.log(this.taskForm.value);
  }
}

<div class="container-fluid page">
  <div class="d-flex page__box p-3 mt-2">Add Task to your Project</div>
  <div class="page__content shadow p-3 position-relative">
    <div class="page__content-data">
<div class="container">
<form [formGroup]="taskForm" (ngSubmit)="onSubmit()">
    <div class="task-container">
    <h2>Create Task</h2>
    <div class="form-group">
            <label for="projectId">Project ID:</label>
            <select id="projectId" name="projectId" [(ngModel)]="selectedProjectId" formControlName="projectId">
              <option value="" disabled>Select Project</option>
              <option *ngFor="let project of projects" [value]="project.project_id">{{ project.project_id }}</option>
            </select>
      <label for="name">Name:</label>
      <input type="text" id="name" formControlName="name">
    </div>

    <div class="form-group1">
      <label for="status">Status:</label>
      <select id="status" formControlName="status">
        <option  value="ON_HOLD">ON_HOLD</option>
        <option  value="IN_PROGRESS">IN_PROGRESS</option>
        <option  value="completed">COMPLETED</option>
      </select>
      <label for="assignedTo">Assigned To:</label>
      <select id="assignedTo" formControlName="assignedTo">
        <option value="" disabled>Select Manager</option>
        <option *ngFor="let user of allUsers" [value]="manager.assignedTo">{{ manager.email }}</option>
      </select>
    </div>


    <div class="form-group">
      <label for="assignDate">Assign Date:</label>
      <input type="date" id="assignDate" formControlName="assignDate">
      <label for="completionDate">Completion Date:</label>
      <input type="date" id="completionDate" formControlName="completionDate">
    </div>

    <button type="submit" >Submit</button>
    <p *ngIf="isCreated">Task is created successfully</p>
  </div>
  </form>
</div>
