import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient,HttpHeaders } from '@angular/common/http';
import { ReactiveFormsModule } from '@angular/forms';

@Component({
  selector: 'app-createproject',
  templateUrl: './createproject.component.html',
  styleUrls: ['./createproject.component.css']
})
export class CreateprojectComponent {

  projectForm: FormGroup;
  isCreated= false;
  constructor(private fb: FormBuilder,private http:HttpClient) {}

  ngOnInit(): void {
    this.projectForm = this.fb.group({
        name: [''],
        status: [''],
        assignDate: [''],
        completionDate: [''],
        assignedTo: [''],
      description:[''],
      departmentName:['']
    });
  // },{ validator: dateValidator });
}

  onSubmit() {
    // Handle form submission
    const token = JSON.parse(sessionStorage.getItem('key')).token;
    const email = JSON.parse(sessionStorage.getItem('key')).email;
    console.log(this.projectForm.value);
    console.log(email);
    let body={
    "name": this.projectForm.value.name,
    "status": this.projectForm.value.status,
    "assignedDate": this.projectForm.value.assignDate,
    "completionDate": this.projectForm.value.completionDate,
    "assignedTo": this.projectForm.value.assignedTo,
    "assignedBy": email,
    "description":this.projectForm.value.description,
    "department_name":this.projectForm.value.departmentName
    };

   console.log(body);
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });
    this.http.post('http://localhost:8083/api/project/create',body, { headers, responseType: 'json' }).subscribe((response)=>{
      console.log(response);
      this.isCreated=true;
      setTimeout(() => {
        this.isCreated = false;
      }, 5000); 
    })

  }
  UpdateProject(){

  }
}
<div class="container-fluid page">
  <div class="d-flex page__box p-3 mt-2">Project</div>
  <div class="page__content shadow p-3 position-relative">
    <div class="page__content-data">
<div class="container">
<form [formGroup]="projectForm" (ngSubmit)="$event.preventDefault(); onSubmit()">
    <div class="task-container">
    <h2>Create Project</h2>
    <div class="form-group">
      <label for="DepartmentName">Department Name</label>
      <select id="DepartmentName" formControlName="departmentName">
        <option  value="Engineering">Engineering</option>
        <option  value="IT Services">IT Services</option>
        <option  value="HR">HR</option>
        <option  value="Consulting">Consulting</option>
        <option  value="Marketing">Marketing</option>
      </select>
      <label for="name">Name:</label>
      <input type="text" id="name" formControlName="name">

      <label for="status">Status:</label>
      <select id="status" formControlName="status">
        <option  value="ON_HOLD">ON_HOLD</option>
        <option  value="IN_PROGRESS">IN_PROGRESS</option>
        <option  value="completed">COMPLETED</option>
      </select>
      <label for="assignedTo">Assigned To:</label>
      <input type="text" id="assignedTo" formControlName="assignedTo">
    </div>

    <div class="form-group">
      <label for="assignDate">Assign Date:</label>
      <input type="date" id="assignDate" formControlName="assignDate">
      <label for="completionDate">Completion Date:</label>
      <input type="date" id="completionDate" formControlName="completionDate">
      <label for="description">Description</label>
      <input type="text" id="description" formControlName="description">
    </div>

    <p *ngIf="isCreated"> is created successfully</p>
    <button type="submit" >Submit</button>
  </div>
  </form>
</div>

<div class="container">
<form [formGroup]="projectForm" (ngSubmit)="$event.preventDefault(); UpdateProject()">
    <div class="task-container">
    <h2>Create Project</h2>
    <div class="form-group">
      <label for="DepartmentName">Department Name</label>
      <select id="DepartmentName" formControlName="departmentName">
        <option  value="Engineering">Engineering</option>
        <option  value="IT Services">IT Services</option>
        <option  value="HR">HR</option>
        <option  value="Consulting">Consulting</option>
        <option  value="Marketing">Marketing</option>
      </select>
      <label for="name">Name:</label>
      <input type="text" id="name" formControlName="name">

      <label for="status">Status:</label>
      <select id="status" formControlName="status">
        <option  value="ON_HOLD">ON_HOLD</option>
        <option  value="IN_PROGRESS">IN_PROGRESS</option>
        <option  value="completed">COMPLETED</option>
      </select>
      <label for="assignedTo">Assigned To:</label>
      <input type="text" id="assignedTo" formControlName="assignedTo">
    </div>

    <div class="form-group">
      <label for="assignDate">Assign Date:</label>
      <input type="date" id="assignDate" formControlName="assignDate">
      <label for="completionDate">Completion Date:</label>
      <input type="date" id="completionDate" formControlName="completionDate">
      <label for="description">Description</label>
      <input type="text" id="description" formControlName="description">
    </div>

    <p *ngIf="isCreated"> is created successfully</p>
    <button type="submit" >Submit</button>
  </div>
  </form>
</div>
