import { Component, OnInit } from '@angular/core';
import { SharedDataService } from '../../../shared-data.service';
@Component({
  selector: 'app-projects',
  templateUrl: './projects.component.html',
  styleUrls: ['./projects.component.css']
})
export class ProjectsComponent implements OnInit {
  constructor(private sharedData : SharedDataService){}
  projectData: any;
  sideBarOpen = true;
  ngOnInit(): void {
      this.projectData = this.sharedData.getProjectDeatils();
  }

  public sideBarToggler()
  {
    this.sideBarOpen = !this.sideBarOpen;
  }
}

<mat-drawer mode="side" [opened]="sideBarOpen">
    <app-sidenav></app-sidenav>
  </mat-drawer>
  <app-header (toggleSidebarForMe)="sideBarToggler()"></app-header>
<div class="container-fluid page">
    <div class="d-flex page__box p-3 mt-2">Projects</div>
    <div class="page__content shadow p-3 position-relative">
      <div class="page__content-data position-absolute text-center">
        <div class="main-data">
        <table>
            <thead>
                <tr>
                    <th>Project ID</th>
                    <th>Project Name</th>
                    <th>Description</th>
                    <th>Status</th>
                    <th>Department Name</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <th>{{ projectData.project_id }}</th>
                    <th>{{ projectData.name }}</th>
                    <th>{{ projectData.description }}</th>
                    <th>{{ projectData.status }}</th>
                    <th> {{ projectData.department_name }}</th>
                </tr>
            </tbody>
        </table>
        </div>
      </div>
    </div>
  </div>
