import { Component, OnInit } from '@angular/core';
import { SharedDataService } from '../../../shared-data.service';

import { HttpClient ,HttpHeaders} from '@angular/common/http';
import { Router } from '@angular/router';

import {Chart, registerables} from 'node_modules/chart.js';
Chart.register(...registerables);
@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent  implements OnInit{


  constructor(private shareddata : SharedDataService,private http:HttpClient,private  router : Router){}

  chartdata : any ;
  labeldata: any[]=[];
  taskCounts: any = {};

  ngOnInit(): void {   

    const token = this.shareddata.getToken();
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });

    this.http.get('http://localhost:8083/api/project/1', { headers, responseType: 'json' }).subscribe(
      (data: any) => {
       console.log(data);
       this.chartdata = data;
       this.shareddata.setProjectDeatils(data);
       this.taskCounts = {
        COMPLETED: 0,
        IN_PROGRESS: 0,
        ON_HOLD: 0
      };

      this.chartdata.tasks.forEach(task => {
        if (task.status in this.taskCounts) {
          this.taskCounts[task.status]++;
        }
      });
      Object.keys(this.taskCounts).forEach(status => {
        if (this.taskCounts[status] > 0) {
          this.labeldata.push(`${status} (${this.taskCounts[status]})`);
        }
      });

      // Render chart
      this.renderChart();

      },
      (error) => {
        console.error('Error fetching data:', error);
      }
    );
  }


  renderChart() {
    const myChart = new Chart('piechart', {
      type: 'pie',
      data: {
        labels: this.labeldata,
        datasets: [{
          label: '# of Tasks',
          data: Object.values(this.taskCounts),
          backgroundColor: [
            'rgba(255, 99, 132, 0.2)',
            'rgba(54, 162, 235, 0.2)',
            'rgba(255, 206, 86, 0.2)'
          ],
          borderColor: [
            'rgba(255, 99, 132, 1)',
            'rgba(54, 162, 235, 1)',
            'rgba(255, 206, 86, 1)'
          ],
          borderWidth: 1
        }]
      },
      options: {
        scales: {
          y: {
            display: false
          }
        }
      }
    });
   }

}
