import { Component,OnInit} from '@angular/core';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
@Component({
  selector: 'app-update-task',
  templateUrl: './update-task.component.html',
  styleUrls: ['./update-task.component.css']
})
export class UpdateTaskComponent implements OnInit{

  projects: any[] = []; // Populate this array with your project data
  updateClicked = false;
  selectedProject: any;
  selectedTaskStatus: string;
  constructor(private http: HttpClient){}
  ngOnInit(): void {
  }
  updateTaskStatus()
  {  
 //  this.updateClicked = true;
   if (this.selectedProject && this.selectedTaskStatus) {
     const token = JSON.parse(sessionStorage.getItem('key')).token;
     const headers = new HttpHeaders({
       'Authorization': `Bearer ${token}`
     });

     // Iterate over tasks to update status
     this.selectedProject.tasks.forEach(task => {
       // Make HTTP POST request to update task status
       this.http.post<any>(`http://localhost:8083/api/task/status/${task.task_id}`, { status: this.selectedTaskStatus }, { headers })
         .subscribe(
           (response) => {
             console.log('Task status updated successfully:', response);
             // Handle response if needed
           },
           (error) => {
             console.error('Error updating task status:', error);
             // Handle error if needed
           }
         );
     });
   } else {
     console.error('No project selected or task status not selected.');
     // Handle case where no project is selected or task status is not selected
   }
 }
}
