import { Component,OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
import { Router } from '@angular/router';
@Component({
  selector: 'app-delete-task',
  templateUrl: './delete-task.component.html',
  styleUrls: ['./delete-task.component.css']
})
export class DeleteTaskComponent implements OnInit{

  taskId:any;
  projectId:any;
  selectedProjectId:number;
  isDeleted:boolean = false;
  projects:any[]=[];
  tasks:any[]=[];
  selectedTaskId:number;
  constructor(private formBuilder: FormBuilder,private http : HttpClient,private router :Router) { }
  ngOnInit(): void {

    const token = JSON.parse(sessionStorage.getItem('key')).token;
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });

    this.http.get<any[]>('http://localhost:8083/api/project/details',{headers}).subscribe((response)=>{
      console.log(response);
      this.projects = response;
      //this.projectslength = response.length;
    })
  }

  DeleteTask(){

    const token = JSON.parse(sessionStorage.getItem('key')).token;
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });

    this.http.delete('http://localhost:8083/api/task/delete/'+this.selectedProjectId+'/task/'+this.selectedTaskId,{headers,responseType:'text'}).subscribe((response)=>{
      console.log(response);
      this.isDeleted = true;
      this.projectId='';
      this.selectedProjectId='';
      setTimeout(()=>{
        this.isDeleted = false;
      },3000);      

    })
  }

  loadTasks(){

    const token = JSON.parse(sessionStorage.getItem('key')).token;
     const headers = new HttpHeaders({
       'Authorization': `Bearer ${token}`
     });

     this.http.get<any[]>('http://localhost:8083/api/project/all/tasks/'+this.selectedProjectId,{headers,responseType:'json'}).subscribe(
     { next:
       (response:any)=>{
       console.log(response.taskId);
         this.tasks = response.taskId;

     },
     error:
     (error:any)=>{
       console.log("error occured ");
     }
     });

  }

}

<div class="container-fluid page">
    <div class="d-flex page__box p-3 mt-2">Delete Tasks in your Project</div>
    <div class="page__content shadow p-3 position-relative">
      <div class="page__content-data">
<form (ngSubmit)="DeleteTask()">
    <div class="user">
    <h1>Delete Task</h1>
    <label>Enter Project Id</label>
     <select id="projectId" name="projectId" [(ngModel)]="selectedProjectId" (change)="loadTasks()" >
              <option value="" disabled>Select Project</option>
              <option *ngFor="let project of projects" [value]="project.project_id">{{ project.project_id }}</option>
            </select>
    <br>
    <br>
    <label>Enter Task Id</label>
   <select id="taskId" [(ngModel)]="selectedTaskId" [ngModelOptions]="{ standalone: true }">
            <option value="" disabled>Select Task</option>
            <option *ngFor="let task of tasks" [value]="task.task_id">{{ task.name }}</option>
    </select>
    <br>
    <p *ngIf="isDeleted">Task is Deleted successfully</p>
    <button class="user-button" type="submit">Confirm</button>
    </div>
</form>
