import { Component,OnInit} from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient ,HttpHeaders} from '@angular/common/http';
import { Router } from '@angular/router';
@Component({
  selector: 'app-update-role',
  templateUrl: './update-role.component.html',
  styleUrls: ['./update-role.component.css']
})
export class UpdateRoleComponent implements OnInit {
  currentRole:any;
  showMessage: boolean = false;
  userid:number;
  constructor(private formBuilder: FormBuilder,private http : HttpClient,private router :Router) { }
  registerForm: FormGroup;
  ngOnInit(): void {
    this.registerForm = this.formBuilder.group({
      userId: ['', [Validators.required]],
      role: ['', Validators.required]
    });
  }

  get f() { return this.registerForm.controls; }

  onSubmit() {
    if (this.registerForm.invalid) {
      return;
    }

    this.userid = this.registerForm.value.userId;
    // Submit the form data
    console.log(this.registerForm.value);
    console.log(this.registerForm.value.role);
  }

  postRole(){

    const token = JSON.parse(sessionStorage.getItem('key')).token
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });


    let body = {
      "role": this.registerForm.value.role
    };
    this.http.post('http://localhost:8082/api/admin/update/role/'+this.userid, body,{headers,responseType:'text'}).subscribe((response)=>{
      console.log(response);
      this.showMessage=true;
    })
  }
}
