import { Component, OnInit } from '@angular/core';
import { SharedDataService } from '../../../shared-data.service';

import { HttpClient ,HttpHeaders} from '@angular/common/http';
import { Router } from '@angular/router';
import { saveAs } from 'file-saver';
import {Chart, registerables} from 'node_modules/chart.js';
Chart.register(...registerables);
@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent  implements OnInit{


  constructor(private shareddata : SharedDataService,private http:HttpClient,private  router : Router){}

  chartdata : any ;
  labeldata: any[]=[];
  realdata : any[] = [];
  colordata : any[] = [];
  projectdata : any = this.shareddata.getProjectDeatils();
  ngOnInit(): void {   

    const token = this.shareddata.getToken();
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });

    this.http.get('http://localhost:8083/api/project/1', { headers, responseType: 'json' }).subscribe(
      (data: any) => {
       console.log(data);
       this.chartdata = data;
       console.log(this.chartdata.tasks[0].status);
       for(let i=0;i<this.chartdata.tasks.length; i++){

        if(this.chartdata.tasks[i].status == 'COMPLETED'){
        this.labeldata.push(this.chartdata.tasks[i].status);
        }
        if(this.chartdata.tasks[i].status == 'IN_PROGRESS'){
          this.labeldata.push(this.chartdata.tasks[i].status);
        }
        if(this.chartdata.tasks[i].status == 'ON_HOLD'){
          this.labeldata.push(this.chartdata.tasks[i].status);
        }
      }
      },
      (error) => {
        console.error('Error fetching data:', error);
      }
    );

    this.RenderChart();
  }


  RenderChart(){
    const mychart = new Chart("piechart", {
      type: 'pie',
      data: {
        labels: ['Task-InComplete', 'Task-Progress', 'Task-Completed'],
        datasets: [{
          label: '# of Votes',
          data: [12, 19, 3],
          borderWidth: 1
        }]
      },
      options: {
        scales: {
          y: {
            beginAtZero: true
          }
        }
      }
    });
   }

}
